package com.baby.app.modules.channel;import com.android.baselibrary.service.bean.home.HomeStarBean;import java.util.List;/** * Created by chen.huarong on 2019-05-07. * 首页和频道桥接人气明星数据 * 1、异步操作需要做主动和被动获取数据 */public class HomeBridge {    private static Callback sCallback;    private static List<HomeStarBean> sStarList;    public static List<HomeStarBean> getStarList() {        return sStarList;    }    public static void setCallback(Callback callback) {        sCallback = callback;    }    public static void callback(List<HomeStarBean> starList) {        sStarList = starList;        if (sCallback != null) {            sCallback.callback(starList);        }    }    public interface Callback {        void callback(List<HomeStarBean> starList);    }}