package com.baby.app.modules.channel.presenter;import com.android.baselibrary.base.BaseCallBack;import com.android.baselibrary.base.BasePresenter;import com.android.baselibrary.base.BaseView;import com.android.baselibrary.base.DefaultView;import com.android.baselibrary.service.NetService;import com.android.baselibrary.service.bean.DefaultBean;import com.android.baselibrary.service.bean.channel.AllTagsBean;import com.android.baselibrary.service.bean.channel.AllTagsDataBean;import java.util.List;/** * Created by chen.huarong on 2019-05-12. */public class AllTagsPresenter extends BasePresenter {    private DefaultView<List<AllTagsBean>> mView;    public AllTagsPresenter(DefaultView<List<AllTagsBean>> view) {        mView = view;    }    @Override    protected BaseView getView() {        return mView;    }    public void fetchAllTags() {        requestDateNew(NetService.getInstance().allTags(), "", new BaseCallBack() {            @Override            public void onSuccess(Object obj) {                if (obj instanceof DefaultBean) {                    DefaultBean<AllTagsDataBean> dataBean = (DefaultBean<AllTagsDataBean>) obj;                    if (dataBean.getData() != null) {                        mView.refresh(dataBean.getData().getCareTagList());                        return;                    }                }                onFaild(obj);            }            @Override            public void onFaild(Object obj) {                mView.showNetError();            }            @Override            public void onNetWorkError(String errorMsg) {                mView.showNetError();            }        });    }}