package com.android.baselibrary.util;import android.graphics.drawable.Drawable;import androidx.annotation.ColorInt;import androidx.annotation.ColorRes;import androidx.annotation.DrawableRes;import com.android.baselibrary.base.BaseApplication;/** * Created by chen.huarong on 2019-05-19. */public class ResourceUtil {    public static Drawable getDrawable(@DrawableRes int drawableId){        return BaseApplication.getInstance().getResources().getDrawable(drawableId);    }    public static int getColor(@ColorRes int colorId){        return BaseApplication.getInstance().getResources().getColor(colorId);    }}